p.foreign:
\begin{align} 
PMLU_{Target} &= \[ \sum_{t \in targetIPA}
    \[  \left\{
    \begin{array}{rr}
        0 & t \textrm{ is a vowel} \\
        1 & t \textrm{ is a consonant} \\
    \end{array}
    \right. \]
\] \\
PMLU_{Actual} &= |actualIPA| \plus \[ \sum_{t,a \in targetIPA,actualIPA}
    \[  \left\{
    \begin{array}{rr}
        0 & t \textrm{ is a vowel} \\
        (stripdiacritics(t) = stripdiacritics(a)) & t \textrm{ is a consonant} \\
    \end{array}
    \right. \]
\] \\
PWP &= \frac{PMLU_{Actual}}{PMLU_{Target}} \\
\end{align}

\begin{align} 
ePMLU_{Target Features} &= \[ \sum_{t \in targetIPA} 
    \[  \left\{
    \begin{array}{rr}
      0 & t \textrm{ is a vowel} \\
      3 & t \textrm{ is a consonant} \\
    \end{array}
    \right. \]
    \] \\
ePMLU_{Actual Features} &=  \[ \sum_{t,a \in targetIPA,actualIPA} 
    \[  \left\{
    \begin{array}{rr}
        0 & t \textrm{ is a vowel} \\
        (manner(t) \equals manner(a)) \plus (place(t) \equals place(a)) \plus (voicing(t) \equals voicing(a)) & t \textrm{ is a consonant} \\
    \end{array}
    \right. \]
\] \\
ePWP_{Features} &= \frac{ePMLU_{Actual Features}}{ePMLU_{Target Features}} \\
\end{align}

\begin{align} 
ePMLU_{Target Syllables} &= \[ \sum_{\sigma \in targetIPA} 
    \[  \left\{
    \begin{array}{rr}
        2 & \sigma \textrm{ is open} \\
        2 \plus closedSyllBonus & \sigma \textrm{ is closed} \\
    \end{array}
    \right. \]
    \] \\
ePMLU_{Actual Syllables} &= \[ \sum_{\sigma \in actualIPA} 
    \[  \left\{
    \begin{array}{rr}
        2 & \sigma \textrm{ is open} \\
        2 \plus closedSyllBonus & \sigma \textrm{ is closed} \\
    \end{array}
    \right. \]
    \] \\    
ePWP\textsubscript{Syllables} &= \frac{ePMLU_{Actual Syllables}}{ePMLU_{Target Syllables}} \\
\end{align}

\begin{align} 
ePMLU_{Target} &= ePMLU_{Target Features} \plus ePMLU_{Target Syllables} \\
ePMLU_{Actual} &= ePMLU_{Actual Features} \plus ePMLU_{Actual Syllables} \\
ePWP &= \frac{ePMLU_{Actual}}{ePMLU_{Target}} \\
\end{align}
