/**
* begin_search
* 
* @param s - the session
*/
function begin_search(s)
{
    // print session name and date and number of records
    out.println( "Session: " + s.name + "\n" + 
                 "   Date: " + s.date + "\n" + 
                 "   Size: " + s.numberOfRecords );
}
